const base_snowflake_url = "https://qqa43113.snowflakecomputing.com";

const prompt = "hello";
const this_message = [{
  role: "user",
  content: [
    { type: "text", text: prompt }
  ]
}];

const request_body = {
  messages: this_message,
  model: "llama3.2-1b",
  temperature: 0.4,
  top_p: 0.6,
};

const headers = {
  "Authorization": `Bearer ${token}`,
  "Content-Type": "application/json",
  "Accept": "application/json, text/event-stream",
};

const url = `${base_snowflake_url}/api/v2/cortex/inference:complete`;

let response, responseBody, requestId;

try {
  response = await $http.post(url, {
    body: request_body,
    headers,
    responseType: 'json',
    resolveWithFullResponse: true,
  });
  responseBody = response.data;
  requestId = response.headers["x-snowflake-request-id"];

  return [{ ...responseBody, request_id: requestId }];

} catch (error) {
  requestId = error.response?.headers?.["x-snowflake-request-id"];
  throw new Error(`Request failed (id: ${requestId}) with status ${error.response?.status}: ${error.response?.data || error.message}`);
}

