const currentItem = $("Trigger").item.json.input;


if (!currentItem.document_query_id || !currentItem.queries) {
  return [{
    json: {
      error: 'Missing required fields: document_query_id or queries',
      received: currentItem
    }
  }];
}

const document_query_id = currentItem.document_query_id;
const queries = currentItem.queries;

if (!Array.isArray(queries)) {
  return [{
    json: {
      error: 'queries is not an array',
      type: typeof queries
    }
  }];
}

return queries.map(query => ({
  json: {
    document_query_id: document_query_id,
    document_query_name: query.document_query_name || '',
    drill_name: query.drill_name || 'unnamed_drill',
    query: query.query || {}
  }
}));

