const results = [];

for (const item of $input.all()) {
  let output = JSON.parse(item.json.output);

  // Handle the new array format
  if (Array.isArray(output)) {
    // Each element in the array is a query pair for a single measure
    for (const queryPair of output) {
      const newItem = {
        json: {
          ...item.json,
          sorted_by_measure: queryPair.sorted_by_measure,
          current_fiscal_quarter_query: queryPair.current_fiscal_quarter_query,
          previous_fiscal_quarter_query: queryPair.previous_fiscal_quarter_query
        }
      };
      delete newItem.json.output;
      results.push(newItem);
    }
  }
  // Backwards compatibility: handle old object format
  else if (typeof output === 'object' && output !== null) {
    if ("current_fiscal_quarter_query" in output) {
      item.json.current_fiscal_quarter_query = output.current_fiscal_quarter_query;
    }
    if ("previous_fiscal_quarter_query" in output) {
      item.json.previous_fiscal_quarter_query = output.previous_fiscal_quarter_query;
    }
    if ("query" in output) {
      item.json.query = output.query;
    }
    if ("error" in output) {
      item.json.error = output.error;
      item.json.details = output.details;
    }
    delete item.json.output;
    results.push(item);
  }
}

return results;

// Access all input items (each is a quarterly analysis object)
const items = $input.all();

// Initialize the merged result with empty arrays for each key
const merged = {
  overall_delta_summary: [],  // Changed to array
  anomalies: [],
  growth_drivers: [],
  declines: [],
  operational_highlights: [],
  recommendations: []
};

// Iterate through all input items and merge them
for (const item of items) {
  const data = item.json.output;

  // Add overall_delta_summary to array
  if (data.overall_delta_summary) {
    merged.overall_delta_summary.push(...data.overall_delta_summary);
  }

  // Merge all array fields by concatenating
  if (data.anomalies && Array.isArray(data.anomalies)) {
    merged.anomalies.push(...data.anomalies);
  }

  if (data.growth_drivers && Array.isArray(data.growth_drivers)) {
    merged.growth_drivers.push(...data.growth_drivers);
  }

  if (data.declines && Array.isArray(data.declines)) {
    merged.declines.push(...data.declines);
  }

  if (data.operational_highlights && Array.isArray(data.operational_highlights)) {
    merged.operational_highlights.push(...data.operational_highlights);
  }

  if (data.recommendations && Array.isArray(data.recommendations)) {
    merged.recommendations.push(...data.recommendations);
  }
}

// Return in n8n format (array with json property)
return [{
  json: merged
}];

const input = $json; // :contentReference[oaicite:2]{index=2}

const BULLET = "• ";

const output = {};

for (const [key, value] of Object.entries(input)) {
  // Expect arrays, but handle edge cases safely
  const arr = Array.isArray(value) ? value : (value == null ? [] : [String(value)]);

  output[key] = arr
    .filter((s) => s != null && String(s).trim() !== "")
    .map((s) => `${BULLET}${String(s)}`)
    .join("\n");
}

// In "Run Once for Each Item", return a single item object
return { json: output };
