function stripCodeFence(s) {
  // Strips Markdown code fence if it exists. Returns the string without the triple-backtick code fence.

  // Non-global regex for detection (avoids lastIndex pitfalls).
  const detect = /```[ \t]*[a-z0-9_+-]*[ \t]*\r?\n[\s\S]*?\r?\n?```/i;
  if (!detect.test(s)) return s;

  // Global/ignore-case regex for actual replacement.
  const replaceRe = /```[ \t]*[a-z0-9_+-]*[ \t]*\r?\n([\s\S]*?)\r?\n?```/gi;
  return s.replace(replaceRe, '$1');
}

