function rename_keys(records, mapping, drop_unmapped = false) {
  return records.map((row) => {
    const new_row = {};
    for (const [k, v] of Object.entries(row)) {
      if (Object.prototype.hasOwnProperty.call(mapping, k)) {
        new_row[mapping[k]] = v;
      } else if (!drop_unmapped) {
        new_row[k] = v;
      }
    }
    return new_row;
  });
}

