// System health checks
const checks = {
  timestamp: new Date().toISOString(),
  memory: process.memoryUsage().heapUsed / 1024 / 1024,
  nodeVersion: process.version,
  uptime: process.uptime()
};

// Simple health validation
const isHealthy = checks.memory < 1024; // Less than 1GB

if (!isHealthy) {
  throw new Error('System health check failed: ' + JSON.stringify(checks));
}

console.log('System health check passed:', checks);
return [{ json: { health: 'OK', checks } }];

// Market hours validation
const now = new Date();
const easternTime = new Date(now.toLocaleString("en-US", {timeZone: "America/New_York"}));
const hour = easternTime.getHours();
const minute = easternTime.getMinutes();
const dayOfWeek = easternTime.getDay();

// Basic market hours check (9:30 AM to 4:00 PM ET, Monday-Friday)
const isWeekday = dayOfWeek >= 1 && dayOfWeek <= 5;
const isMarketHours = (hour > 9 || (hour === 9 && minute >= 30)) && hour < 16;

if (!isWeekday) {
  throw new Error('Market closed: Weekend');
}

if (!isMarketHours) {
  throw new Error('Market closed: Outside market hours');
}

const timeToClose = (16 - hour) * 60 - minute;
console.log('Market is open. Time to close:', timeToClose, 'minutes');

return [{ json: { continue: true, marketStatus: 'OPEN', timeToClose: timeToClose, easternTime: easternTime.toISOString() } }];

// Initialize trading session
const sessionId = 'ib_' + new Date().toISOString().replace(/[:.]/g, '_');
const config = {
  sessionId: sessionId,
  maxPositions: 3,
  maxCorrelation: 0.6,
  positionSizePercent: 0.05,
  minCreditTarget: 0.10,
  maxWingWidth: 0.03,
  minIVRank: 30,
  maxIVRank: 70,
  targetDTE: 45,
  minDTE: 21,
  profitTarget: 0.25,
  stopLoss: 2.0,
  deltaThreshold: 0.10
};

console.log('Trading session initialized:', sessionId);
return [{ json: { sessionId: sessionId, config: config, status: 'INITIALIZED', timestamp: new Date().toISOString() } }];

// Mock stock universe for demo
const stockUniverse = [
  { symbol: 'AAPL', price: 175.50, avgVolume: 50000000, optionsVolume: 500000, sector: 'Technology' },
  { symbol: 'MSFT', price: 420.25, avgVolume: 25000000, optionsVolume: 300000, sector: 'Technology' },
  { symbol: 'GOOGL', price: 152.80, avgVolume: 30000000, optionsVolume: 250000, sector: 'Technology' },
  { symbol: 'TSLA', price: 248.75, avgVolume: 75000000, optionsVolume: 800000, sector: 'Automotive' },
  { symbol: 'NVDA', price: 135.40, avgVolume: 45000000, optionsVolume: 600000, sector: 'Technology' },
  { symbol: 'META', price: 558.20, avgVolume: 20000000, optionsVolume: 180000, sector: 'Technology' },
  { symbol: 'AMZN', price: 185.30, avgVolume: 35000000, optionsVolume: 220000, sector: 'Consumer' },
  { symbol: 'SPY', price: 573.25, avgVolume: 80000000, optionsVolume: 2000000, sector: 'ETF' }
];

// Filter for liquid stocks suitable for Iron Butterfly
const liquidStocks = stockUniverse.filter(stock => {
  return stock.avgVolume > 10000000 && 
         stock.optionsVolume > 100000 && 
         stock.price > 50 && 
         stock.price < 600;
});

console.log('Loaded stock universe:', liquidStocks.length, 'stocks');
return [{ json: { universe: liquidStocks, count: liquidStocks.length, timestamp: new Date().toISOString() } }];

const stocks = $input.all()[0].json.universe;
const batchSize = Math.ceil(stocks.length / 5);
const batches = [];

for (let i = 0; i < 5; i++) {
  const start = i * batchSize;
  const end = Math.min(start + batchSize, stocks.length);
  if (start < stocks.length) {
    batches.push({
      agentId: i + 1,
      symbols: stocks.slice(start, end),
      batchSize: end - start,
      priority: i === 0 ? 'high' : 'normal'
    });
  }
}

console.log('Created', batches.length, 'work batches for agents');
return batches;

// Collect results from all research agents
const allResults = $input.all();
const opportunities = [];

for (const batch of allResults) {
  if (batch.json && batch.json.symbols) {
    for (const symbol of batch.json.symbols) {
      // Mock analysis results
      const wingWidth = symbol.price * 0.025; // 2.5% wing width
      const creditReceived = Math.random() * 1.5 + 0.50; // $0.50-2.00
      const maxLoss = wingWidth - creditReceived;

      if (maxLoss > 0 && creditReceived > 0.50) {
        opportunities.push({
          symbol: symbol.symbol,
          atmStrike: Math.round(symbol.price),
          shortCallStrike: Math.round(symbol.price),
          shortPutStrike: Math.round(symbol.price),
          longCallStrike: Math.round(symbol.price + wingWidth),
          longPutStrike: Math.round(symbol.price - wingWidth),
          creditReceived: creditReceived,
          maxLoss: maxLoss,
          delta: (Math.random() - 0.5) * 0.1, // Random delta -0.05 to 0.05
          theta: Math.random() * 0.05 + 0.02,
          ivRank: Math.random() * 100,
          technicalScore: Math.random() * 100,
          liquidityScore: Math.min(100, symbol.optionsVolume / 10000),
          compositeScore: Math.random() * 100
        });
      }
    }
  }
}

// Sort by composite score
opportunities.sort((a, b) => b.compositeScore - a.compositeScore);

console.log('Found', opportunities.length, 'Iron Butterfly opportunities');
return [{ json: { opportunities: opportunities.slice(0, 10), totalAnalyzed: allResults.length, totalPassed: opportunities.length } }];

const opportunities = $input.all()[0].json.opportunities;
const config = $('Initialize Trading Session').item.json.config;
const selected = [];

// Select top 3 non-correlated opportunities
for (const opp of opportunities) {
  if (selected.length >= config.maxPositions) break;

  // Simple correlation check (mock - in reality would check sector/beta)
  let highCorrelation = false;
  for (const sel of selected) {
    if (opp.symbol.substring(0, 2) === sel.symbol.substring(0, 2)) {
      highCorrelation = true;
      break;
    }
  }

  if (!highCorrelation && opp.creditReceived >= config.minCreditTarget) {
    const accountValue = 100000; // Mock $100k account
    const maxPositionSize = accountValue * config.positionSizePercent;
    const numberOfContracts = Math.floor(maxPositionSize / (opp.maxLoss * 100));

    if (numberOfContracts >= 1) {
      selected.push({
        ...opp,
        positionSize: numberOfContracts * opp.maxLoss * 100,
        numberOfContracts: numberOfContracts
      });
    }
  }
}

console.log('Selected', selected.length, 'trades for execution');
return [{ json: { selectedTrades: selected, rejectedDueToCorrelation: opportunities.length - selected.length } }];

const selectedTrades = $input.all()[0].json.selectedTrades;
const executedTrades = [];
const failedTrades = [];

for (const trade of selectedTrades) {
  try {
    // Mock trade execution
    const orderSuccess = Math.random() > 0.1; // 90% success rate

    if (orderSuccess) {
      const executedTrade = {
        ...trade,
        orderId: 'IB_' + Date.now() + '_' + trade.symbol,
        executionTime: new Date().toISOString(),
        status: 'FILLED',
        actualCredit: trade.creditReceived * (0.95 + Math.random() * 0.1), // 95-105% of target
        legs: [
          { action: 'SELL', strike: trade.shortCallStrike, type: 'CALL', quantity: trade.numberOfContracts },
          { action: 'SELL', strike: trade.shortPutStrike, type: 'PUT', quantity: trade.numberOfContracts },
          { action: 'BUY', strike: trade.longCallStrike, type: 'CALL', quantity: trade.numberOfContracts },
          { action: 'BUY', strike: trade.longPutStrike, type: 'PUT', quantity: trade.numberOfContracts }
        ]
      };
      executedTrades.push(executedTrade);
      console.log('Executed Iron Butterfly on', trade.symbol);
    } else {
      failedTrades.push({ ...trade, error: 'Order rejected by broker' });
      console.log('Failed to execute trade on', trade.symbol);
    }
  } catch (error) {
    failedTrades.push({ ...trade, error: error.message });
  }
}

return [{ json: { executedTrades: executedTrades, failedTrades: failedTrades, executionSummary: { successful: executedTrades.length, failed: failedTrades.length } } }];

const executionResult = $input.all()[0].json;
const executedTrades = executionResult.executedTrades || [];
const monitoringActions = [];

for (const trade of executedTrades) {
  // Mock position monitoring
  const currentPrice = trade.atmStrike * (0.98 + Math.random() * 0.04); // Â±2% price movement
  const timeDecay = Math.random() * 0.02; // Random time decay
  const currentPnL = (trade.actualCredit - timeDecay) * trade.numberOfContracts * 100;
  const pnlPercent = currentPnL / (trade.maxLoss * trade.numberOfContracts * 100);

  let action = 'HOLD';
  let reason = 'Position within parameters';

  // Check exit conditions
  if (pnlPercent >= 0.25) {
    action = 'CLOSE';
    reason = 'Profit target reached (25%)';
  } else if (currentPnL <= -2 * trade.actualCredit * trade.numberOfContracts * 100) {
    action = 'CLOSE';
    reason = 'Loss limit reached';
  } else if (Math.abs(currentPrice - trade.atmStrike) / trade.atmStrike > 0.05) {
    action = 'ADJUST';
    reason = 'Price moved outside comfort zone';
  }

  monitoringActions.push({
    symbol: trade.symbol,
    orderId: trade.orderId,
    action: action,
    reason: reason,
    currentPnL: currentPnL,
    currentPnLPercent: pnlPercent,
    currentPrice: currentPrice,
    originalStrike: trade.atmStrike
  });
}

console.log('Position monitoring completed for', monitoringActions.length, 'positions');
return [{ json: { monitoringActions: monitoringActions, timestamp: new Date().toISOString() } }];

const error = $input.all()[0].json;
console.log('Error caught:', error);

// Log error and continue workflow
const errorLog = {
  timestamp: new Date().toISOString(),
  errorType: 'WORKFLOW_ERROR',
  errorMessage: error.message || 'Unknown error',
  source: error.source || 'Unknown',
  severity: 'MEDIUM',
  recovered: true
};

return [{ json: { error: errorLog, action: 'CONTINUE', message: 'Error logged and workflow continuing' } }];

const monitoringData = $input.all()[0].json;
const sessionConfig = $('Initialize Trading Session').item.json.config;

// Generate performance report
const report = {
  sessionId: sessionConfig.sessionId,
  timestamp: new Date().toISOString(),
  summary: {
    totalOpportunities: $('Aggregate Research Results').item.json.totalPassed || 0,
    tradesSelected: $('Select Top Trades').item.json.selectedTrades?.length || 0,
    tradesExecuted: $('Execute Trades').item.json.executionSummary?.successful || 0,
    tradesFailed: $('Execute Trades').item.json.executionSummary?.failed || 0,
    positionsRequiringAction: monitoringData.monitoringActions?.filter(a => a.action !== 'HOLD').length || 0
  },
  performance: {
    totalPnL: monitoringData.monitoringActions?.reduce((sum, action) => sum + (action.currentPnL || 0), 0) || 0,
    averagePnLPercent: monitoringData.monitoringActions?.length > 0 ? 
      monitoringData.monitoringActions.reduce((sum, action) => sum + (action.currentPnLPercent || 0), 0) / monitoringData.monitoringActions.length : 0,
    positionsAtRisk: monitoringData.monitoringActions?.filter(a => a.currentPnLPercent < -0.5).length || 0
  },
  nextActions: monitoringData.monitoringActions?.filter(a => a.action !== 'HOLD') || [],
  systemHealth: 'OPERATIONAL',
  marketStatus: $('Market Hours Validation').item.json.marketStatus || 'UNKNOWN'
};

console.log('Iron Butterfly session report generated');
return [{ json: report }];
