const { PromptTemplate } = require('@langchain/core/prompts');

const query = `
<task>
Use the extracted text from a resume to put the information into a JSON structured output.
</task>

<output_format>
Make sure to output in the following JSON format:
{{ $('Globals').item.json.resume_example }}
</output_format>



<context>
- The resume text will contain the person's name and contact details in the header, in the beginning. 
- Then there will be an Experience section that lists out the different companies and roles the person has been in, along with the timeline for each role. 
- Then there will be a section of Education.
- Then there will be a section for Skillset.

<resume>
{{ $('Extract Resume Content String from File').item.json.text }}
</resume>

</context>

/no_think

`;
const prompt = PromptTemplate.fromTemplate(query);

// If you are allowing more than one language model input connection (-1 or
// anything greater than 1), getInputConnectionData returns an array, so you
// will have to change the code below it to deal with that. For example, use
// llm[0] in the chain definition

const llm = await this.getInputConnectionData('ai_languageModel', 0);
let chain = prompt.pipe(llm);
const output = await chain.invoke();
return [ {json: { output } } ];
